mapper.select:
select

from cskaoyan_mall_coupon_user
where coupon_id = #{couponId,jdbcType=INTEGER}

  and status = #{status}


  and user_id = #{userId}

mapper.update:
update cskaoyan_mall_coupon_user
set deleted = 1
where coupon_id = #{couponId}

mapper.insert:
insert into cskaoyan_mall_coupon_user (coupon_id,user_id,
  status, start_time,
  end_time,add_time
 )
values ( #{couponId,jdbcType=INTEGER},#{userId,jdbcType=INTEGER},
  #{status,jdbcType=SMALLINT}, #{startTime,jdbcType=TIMESTAMP},
  #{endTime,jdbcType=TIMESTAMP}, #{addTime,jdbcType=TIMESTAMP}
)
